import { buildSchema} from "graphql";

const schema = buildSchema(`
    type Product {
        id: ID!
        name: String
        description: String
        price: Float
        soldout: Soldout
        inventory: Int
        stores: [Store]
    }

    enum Soldout {
        SOLDOUT
        ONSTOCK
    }

    scalar SoldoutValue

    type Store {
        id: ID!
        store: String
    }

    type Query {
        getProduct(id: ID): Product
        getAllProducts: [Product]
    }

    input StoreInput {
        id: ID
        store: String
    }

    input ProductInput {
        id: ID
        name: String
        description: String
        price: Float
        soldout: Soldout
        inventory: Int
        stores: [StoreInput]
    }

    type Mutation {
        createProduct(input: ProductInput): Product
    }

`);

export default schema;

